sys_script_fix.script:
function logObj(currentObj, indicator) {
	var str ='Object Contents:\n';
    for (key in currentObj) {
        if (typeof currentObj[key] != "function") {
            str =  str + key + ":: " + currentObj[key].toString() +'\n';
        } else {
            logObj(currentObj[key], indicator);
        }
    }
	gs.info(indicator + str);
}
//test script
var str = '';
var current=[];
current[0] = 'A';
current[1] = 'B';
current[2] = 'C';
logObj(current, '****');
